import React from "react";
import './signin.scss';
import FormInput from "../form-input/form";
class SignIn extends React.Component{
    constructor(props){
        super(props)

        this.state ={
            email:'',
            password:''
        }
    }

    handleSubmit = event => {
        event.preventDefault();
    
        this.setState({ email: '', password: '' });
      };

    handleChange=event=>{
        const {value,name} = event.target;
        this.setState({[name]:value});

    };

    

    render(){
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email id and password</span>

                <form onSubmit={this.handleSubmit} >
                    <FormInput name="email" type="email" onChange={this.handleChange} value={this.state.email} label='Email' required />
                    
                    <FormInput type="password" name="password" value={this.state.password} onChange={this.handleChange} label='Password' required />
                    
                    <FormInput type="submit" value="Submit Form" />
                </form>


            </div>
        );
    }
}
export default SignIn;
